mapper.select:
SELECT cl.id, cl.title, cl.classification_id, cl.teacher, cl.cover, cl.description, cl.price, cl.level, cl.suitable, cl.effect, cl.status, cl.rate, c.title AS classification_title FROM class cl JOIN classification c ON c.id = cl.classification_id
AND cl.title like #{title}
LIMIT #{pageStart}, #{pageSize}

mapper.update:
UPDATE class SET title = #{title} , classification_id = #{classification_id} ,teacher = #{teacher},effect = #{effect} ,
cover = #{cover}  ,description = #{description}  ,price = #{price} ,level = #{level} ,suitable = #{suitable}
WHERE id = #{id}
